package org.example.model;import jakarta.persistence.Column;import jakarta.persistence.Entity;import jakarta.persistence.Id;import jakarta.persistence.Table;@Table@Entity(name = "Cards")public class Cards {    @Id    @Column(name = "cardNumber", unique = true, nullable = false, length = 16)    private String cardNumber;    @Column(name = "accountNumber")    private String accountNumber;    @Column(name = "type")    private String type;    @Column(name = "pin")    private int pin;    @Column(name = "cvv")    private int cvv;    public String getCardNumber() {        return cardNumber;    }    public void setCardNumber(String cardNumber) {        this.cardNumber = cardNumber;    }    public String getAccountNumber() {        return accountNumber;    }    public void setAccountNumber(String accountNumber) {        this.accountNumber = accountNumber;    }    public String getType() {        return type;    }    public void setType(String type) {        this.type = type;    }    public int getPin() {        return pin;    }    public void setPin(int pin) {        this.pin = pin;    }    public int getCvv() {        return cvv;    }    public void setCvv(int cvv) {        this.cvv = cvv;    }}