package org.example.model;import jakarta.persistence.Column;import jakarta.persistence.Entity;import jakarta.persistence.Id;import jakarta.persistence.Table;@Table@Entity(name = "loan")public class Loan {    @Id    @Column(name = "loanID", unique = true, nullable = false, length = 20)    private String loanID;    @Column(name = "accountNumber")    private String accountNumber;    @Column(name = "schemeName")    String schemeName;    @Column(name = "sanctionedAmount")    private double sanctionedAmount;    @Column(name = "paidAmount")    private double paidAmount;    @Column(name = "intrest")    private double intrest;    @Column(name = "dueAmount")    private double dueAmount;    @Column(name = "sanctionedDate")    private String sanctionedDate;    @Column(name = "term")    private int term;    @Column(name = "loanType")    private String loanType;    @Column(name = "status")    private String loanStatus;    public String getLoanID() {        return loanID;    }    public void setLoanID(String loanID) {        this.loanID = loanID;    }    public String getAccountNumber() {        return accountNumber;    }    public void setAccountNumber(String accountNumber) {        this.accountNumber = accountNumber;    }    public String getSchemeName() {        return schemeName;    }    public void setSchemeName(String schemeName) {        this.schemeName = schemeName;    }    public double getSanctionedAmount() {        return sanctionedAmount;    }    public void setSanctionedAmount(double sanctionedAmount) {        this.sanctionedAmount = sanctionedAmount;    }    public double getPaidAmount() {        return paidAmount;    }    public void setPaidAmount(double paidAmount) {        this.paidAmount = paidAmount;    }    public double getIntrest() {        return intrest;    }    public void setIntrest(double intrest) {        this.intrest = intrest;    }    public double getDueAmount() {        return dueAmount;    }    public void setDueAmount(double dueAmount) {        this.dueAmount = dueAmount;    }    public String getSanctionedDate() {        return sanctionedDate;    }    public void setSanctionedDate(String sanctionedDate) {        this.sanctionedDate = sanctionedDate;    }    public int getTerm() {        return term;    }    public void setTerm(int term) {        this.term = term;    }    public String getLoanStatus() {        return loanStatus;    }    public void setLoanStatus(String loanStatus) {        this.loanStatus = loanStatus;    }    public String getLoanType() {        return loanType;    }    public void setLoanType(String loanType) {        this.loanType = loanType;    }}