package org.example.model;import jakarta.persistence.*;import java.time.LocalDateTime;@Table@Entity(name = "user")public class User {    @Id    @Column(name = "username",unique = true,nullable = false,length = 64)    private String username;    @Column(name = "password")    private String password;    @Column(name="accountNumber")    private String accountNumber;    public String getUsername() {        return username;    }    public void setUsername(String username) {        this.username = username;    }    public String getPassword() {        return password;    }    public void setPassword(String password) {        this.password = password;    }    public String getAccountNumber() {        return accountNumber;    }    public void setAccountNumber(String accountNumber) {        this.accountNumber = accountNumber;    }}