package org.example.utils;import com.fasterxml.jackson.databind.ObjectMapper;import org.example.model.Account;import java.io.File;import java.io.IOException;public class AccountUtils {    public static Account getAccountDataFromJSON(String fileName) {        Account account;        try {            ObjectMapper objectMapper = new ObjectMapper();            String filePath = "src/main/resources/Account/" + fileName;            File jsonData = new File(filePath);            account = objectMapper.readValue(jsonData, Account.class);        } catch (IOException e) {            throw new RuntimeException(e);        }        return account;    }}