package org.example.utils;import org.example.model.Account;import org.example.model.Employee;import org.hibernate.Session;import java.util.List;import java.util.Objects;public class EmployeeUtils {    public static String validateEmployeeDetails(Session session, Employee employee) {        String valMsg = null;        boolean accountFound = false;        if (!employee.isSuperUser()) {            valMsg = "Employee should be super user.";        }        if (employee.getPassword() == null) {            valMsg = "Password field cannot be null";        }        if (employee.getPassword().length() < 8) {            valMsg = "Password length be of minimum of 8";        }        return valMsg;    }}